
define('text!widgets/table/CheckboxHeaderCell/_checkboxHeaderCell.html',[],function () { return '<th class="elWidgets-TableCheckboxHeaderCell">\n    <div class="elWidgets-TableCheckboxHeaderCell-wrap">\n        <input class="ebCheckbox" type="checkbox" value="1"/>\n        <span class="ebCheckbox-inputStatus"></span>\n    </div>\n</th>';});

define('styles!widgets/table/CheckboxHeaderCell/_checkboxHeaderCell.less',[],function () { return '.elWidgets-TableCheckboxHeaderCell {\n  width: 12px;\n}\n.elWidgets-TableCheckboxHeaderCell-wrap {\n  text-align: center;\n  width: 100%;\n  margin-left: -4px;\n}\n';});

/*global define*/
define('widgets/table/CheckboxHeaderCell/CheckboxHeaderCellView',[
    'jscore/core',
    'text!./_checkboxHeaderCell.html',
    'styles!./_checkboxHeaderCell.less'
], function (core, template, styles) {
    

    return core.View.extend({

        afterRender: function () {
            this.checkbox = this.getElement().find('.ebCheckbox');
        },

        getTemplate: function () {
            return template;
        },

        getStyle: function () {
            return styles;
        },

        getCheckbox: function () {
            return this.checkbox;
        }

    });


});

/*global define*/
define('widgets/table/CheckboxHeaderCell/CheckboxHeaderCell',[
    'widgets/table/Cell',
    './CheckboxHeaderCellView'
], function (Cell, View) {
    

    /**
     * Provides a header cell with a checkbox. When the checkbox is checked, all of the checkboxes in the same column
     * will be checked. This cell should be used in conjunction with the CheckboxCell.
     *
     * @class table.CheckboxHeaderCell
     * @extends table.Cell
     * @beta
     */
    return Cell.extend({

        View: View,

        onCellReady: function () {
            this.getEventBus().subscribe('CheckboxHeaderCell:uncheck-header', function () {
                this.view.getCheckbox().setProperty('checked', false);
            }.bind(this));

            this.view.getCheckbox().addEventHandler('click', function () {
                var checked = this.view.getCheckbox().getProperty('checked');
                this.getEventBus().publish('CheckboxCell:check', checked);
            }.bind(this));
        }

    });

});

define('widgets/table/CheckboxHeaderCell', ['widgets/table/CheckboxHeaderCell/CheckboxHeaderCell'], function (main) { return main; });
