
define('text!widgets/table/HeaderCell/_headerCell.html',[],function () { return '<th></th>';});

/*global define*/
define('widgets/table/HeaderCell/HeaderCellView',[
    'jscore/core',
    'text!./_headerCell.html'
], function (core, template) {
    

    return core.View.extend({

        getTemplate: function () {
            return template;
        },

        getBody: function () {
            return this.getElement();
        },

        setValue: function (value) {
            this.getBody().setText(value);
        },

        getValue: function () {
            return this.getBody().getText();
        }

    });

});

/*global define*/
define('widgets/table/HeaderCell/HeaderCell',[
    'widgets/table/Cell',
    './HeaderCellView'
], function (Cell, View) {
    

    /**
     * Basic HeaderCell class.
     *
     * @class table.HeaderCell
     * @extends table.Cell
     * @beta
     */
    return Cell.extend({

        View: View,

        onViewReady: function () {
            this.setValue(this.options.column.getTitle());
            this.onCellReady();
        }

    });

});

define('widgets/table/HeaderCell', ['widgets/table/HeaderCell/HeaderCell'], function (main) { return main; });
