
define('text!widgets/table/HeaderRow/_headerRow.html',[],function () { return '<thead>\n    <tr></tr>\n</thead>\n';});

/*global define*/
define('widgets/table/HeaderRow/HeaderRowView',[
    'jscore/core',
    'text!./_headerRow.html'
], function (core, template) {
    

    return core.View.extend({

        getTemplate: function () {
            return template;
        },

        getBody: function () {
            return this.getElement().children()[0];
        }

    });

});

/*global define*/
define('widgets/table/HeaderRow/HeaderRow',[
    'widgets/table/Row',
    './HeaderRowView'
], function (Row, View) {
    

    /**
     * Basic HeaderRow class
     *
     * @class table.HeaderRow
     * @extends table.Row
     * @beta
     */
    return Row.extend({

        View: View

    });

});

define('widgets/table/HeaderRow', ['widgets/table/HeaderRow/HeaderRow'], function (main) { return main; });
